@Component({
  selector: 'app-donut-form',
  templateUrl: './donut-form.component.html',
  styleUrls: ['./donut-form.component.scss']
})
export class DonutFormComponent {
  icons:string[]=[
    'caramel-swirl',
    'glazed-fudge',
    'just-chovolate',
    'sour-supreme',
    'strawberry-glaze',
    'vanilla-sundae',
  ]

}
